mapper.select:
select sum(case when re.etype = 30000 then pre.rdDepreciation else 0 end) as prod, sum(case when re.etype = 30100 then pre.rdDepreciation else 0 end) as lab, sum(case when re.etype = 40001 then pre.rdDepreciation else 0 end) as assets, sum(case when pre.powerRate is not null then pre.powerRate else 0 end ) as power, pre.`month`,pre.projectId from p_rdEquipment pre INNER JOIN rdEquipment re on re.companyId=pre.companyId and re.ecode=pre.ecode and re.year=#{year} where pre.projectId in (#{pId}) and pre.month in
#{m}
group by projectId,`month`

select id,ecode,`month` from p_rdEquipment where projectId = #{projectId}
and `month` in (#{m})
and ecode in (#{ecode})

select ie.effect, e.ename, e.emodal, ie.ecode, pr.rdHour as monthHour
from i_equipment ie
         INNER JOIN rdEquipment re on re.companyId = ie.companyId and re.ecode = ie.ecode and re.year = ie.year
         INNER join equipment e on ie.ecode = e.ecode and ie.companyId = e.companyId
         INNER join p_rdEquipment pr
                    on ie.ecode = pr.ecode and ie.companyId = pr.companyId and ie.projectId = pr.projectId
where ie.projectId = #{projectId}
  and ie.companyId = #{companyId}
  and pr.month = #{month}
  and re.etype != 40001
  and ie.year = #{year}
