mapper.select:
select SUM(CASE WHEN s.rdType div 100 = 100 OR s.rdType div 100 = 101 THEN s.rdFunds ELSE 0 END) AS k10000, SUM(CASE WHEN s.rdType div 100 = 300 OR s.rdType div 100 = 301 THEN s.rdFunds ELSE 0 END) AS k30000, SUM(CASE WHEN s.rdType div 100 = 200 THEN s.rdFunds ELSE 0 END) AS k20000, SUM(CASE WHEN s.rdType div 100 = 201 THEN s.rdFunds ELSE 0 END) AS k20100, SUM(CASE WHEN s.rdType div 100 = 202 THEN s.rdFunds ELSE 0 END) AS k20200, SUM(CASE WHEN s.rdType div 100 = 203 THEN s.rdFunds ELSE 0 END) AS k20300, SUM(CASE WHEN s.rdType div 100 = 206 THEN s.rdFunds ELSE 0 END) AS k20600, SUM(CASE WHEN s.rdType div 100 = 205 THEN s.rdFunds ELSE 0 END) AS k20500, SUM(CASE WHEN s.rdType div 100 = 207 THEN s.rdFunds ELSE 0 END) AS k20700, SUM(CASE WHEN s.rdType div 100 = 500 THEN s.rdFunds ELSE 0 END) AS k50000, SUM(CASE WHEN s.rdType div 100 = 400 OR s.rdType div 100 = 401 OR s.rdType div 100 = 402 THEN s.rdFunds ELSE 0 END) AS k40000, SUM(CASE WHEN s.rdType div 100 = 604 THEN s.rdFunds ELSE 0 END) AS k60400, SUM(CASE WHEN s.rdType div 100 = 699 THEN s.rdFunds ELSE 0 END) AS k69900 from p_summary as s inner join p_project AS p on p.id = s.projectId where s.projectId in (select p.id from p_project AS p where
p.companyId = #{query.companyId}
and p.endDate >= #{beginMonth}
and p.beginDate <= #{endMonth} )
and s.month = #{query.month}
order by s.projectId

select SUM(CASE WHEN s.rdType div 100 = 100 OR s.rdType div 100 = 101 THEN s.rdFunds ELSE 0 END) AS k10000, SUM(CASE WHEN s.rdType div 100 = 300 OR s.rdType div 100 = 301 THEN s.rdFunds ELSE 0 END) AS k30000, SUM(CASE WHEN s.rdType div 100 = 200 THEN s.rdFunds ELSE 0 END) AS k20000, SUM(CASE WHEN s.rdType div 100 = 201 THEN s.rdFunds ELSE 0 END) AS k20100, SUM(CASE WHEN s.rdType div 100 = 202 THEN s.rdFunds ELSE 0 END) AS k20200, SUM(CASE WHEN s.rdType div 100 = 203 THEN s.rdFunds ELSE 0 END) AS k20300, SUM(CASE WHEN s.rdType div 100 = 206 THEN s.rdFunds ELSE 0 END) AS k20600, SUM(CASE WHEN s.rdType div 100 = 205 THEN s.rdFunds ELSE 0 END) AS k20500, SUM(CASE WHEN s.rdType div 100 = 207 THEN s.rdFunds ELSE 0 END) AS k20700, SUM(CASE WHEN s.rdType div 100 = 500 THEN s.rdFunds ELSE 0 END) AS k50000, SUM(CASE WHEN s.rdType div 100 = 400 OR s.rdType div 100 = 401 OR s.rdType div 100 = 402 THEN s.rdFunds ELSE 0 END) AS k40000, SUM(CASE WHEN s.rdType div 100 = 604 THEN s.rdFunds ELSE 0 END) AS k60400, SUM(CASE WHEN s.rdType div 100 = 699 THEN s.rdFunds ELSE 0 END) AS k69900 from p_summary as s inner join p_project AS p on p.id = s.projectId where s.projectId in (select p.id from p_project AS p where
p.companyId = #{query.companyId}
and p.endDate >= #{beginMonth}
and p.beginDate <= #{endMonth} )
and s.month >= #{beginYear}
and s.month <= #{query.month}
order by s.projectId

SELECT month,SUM(rdFunds) totalData,rdType
from p_summary
WHERE `month` >=#{yearFirstDay} AND `month` <=#{yearLastDay}

    and rdType not in

        #{item}


GROUP BY rdType,`month`

select projectId,rdType,sum(rdFunds) rdFunds
from p_summary
where projectId=#{projectId}

    and `month` >= #{beginDate} and `month` <= #{endDate}

group by rdType,projectId

select dm.id,dm.remainQuantity remain,dm.quantity total,pm.used rdUsed from p_material pm
inner join d_material dm on dm.id=pm.materialDataId
where pm.projectId=#{projectId}

select de.id,de.remainAmount remain,de.amount total,pe.rdAmount rdUsed from p_energy pe
inner join d_energy de on de.id=pe.energyDataId
where pe.projectId=#{projectId}

select dd.id,dd.remainDFee remain,dd.dFee total,pd.rdAmount rdUsed from p_design pd
inner join d_design dd on dd.id=pd.designDataId
where pd.projectId=#{projectId}

select di.id,di.remainExpense remain,di.expense total,pi.rdAmount rdUsed from p_inspection pi
inner join d_inspection di on di.id=pi.inspectionDataId
where pi.projectId=#{projectId}

SELECT ps.projectId,ps.`month`,ps.rdType,ps.rdFunds,p.parentId
from p_summary ps
INNER JOIN p_project p on p.id = ps.projectId
WHERE p.companyId = #{companyId} and ps.`month` <= #{endMonth} and ps.`month` >= #{beginMonth}

    and ps.projectId=#{projectId}

order by ps.`month`,ps.rdType

select projectId,sum(rdFunds) as rdFunds from p_summary where projectId in

    #{projectId}

and month <= #{endDate} and month >= #{beginDate}
group by projectId
