package com.yskc.rs.enums;import java.util.HashMap;import java.util.Map;/** * 员工类型枚举类 * * @author wangxing */public enum EmployeeTypeEnum {    /**     * 普通员工     */    ORDINARY_EMPLOYEES(0, ""),    /**     * 研发人员     */    THERESEARCHERS(1, "研究人员"),    /**     * 技术人员     */    TECHNICALPERSONNEL(2, "技术人员"),    /**     * 辅助人员     */    SUPPORTSTAFF(3, "辅助人员");    private Integer value;    private String type;    EmployeeTypeEnum(int value, String type) {        this.value = value;        this.type = type;    }    public static Map<Integer,String> valueType = new HashMap<>();    static{        for (EmployeeTypeEnum employeeTypeEnumEnum : values()) {            valueType.put(employeeTypeEnumEnum.value,employeeTypeEnumEnum.type);        }    }    public int getValue() {        return value;    }    public String getType() {        return type;    }    public static EmployeeTypeEnum getEmployeeTypeEnum(Integer value) {        if (null == value) {            return ORDINARY_EMPLOYEES;        }        for (EmployeeTypeEnum employeeTypeEnumEnum : values()) {            if (employeeTypeEnumEnum.value.equals(value)) {                return employeeTypeEnumEnum;            }        }        return ORDINARY_EMPLOYEES;    }    public static EmployeeTypeEnum getEmployeeTypeEnum(String type) {        if (null == type) {            return ORDINARY_EMPLOYEES;        }        for (EmployeeTypeEnum employeeTypeEnumEnum : values()) {            if (employeeTypeEnumEnum.type.equals(type)) {                return employeeTypeEnumEnum;            }        }        return ORDINARY_EMPLOYEES;    }    public static String getEmployeeType(Integer value) {        return valueType.get(value);    }}