mapper.select:
select p.id, p.patentNo,p.patentName,p.mainType,p.applyDateTime,p.inventor,p.authDate, p.caseStatus,p.expiryDate,p.expiryAmount,p.projectId,pp.pname,pa.applyName,pp.beginYear,pp.rdTitle,p.source, p.claimNum,p.usedCnt,p.summary from patent p left join p_project pp on p.projectId=pp.id left join patent_apply pa on p.patentNo=pa.patentNo where p.companyId=#{companyId}
and p.patentNo like "%"#{query.patentNo}"%"
and p.patentName like "%"#{query.patentName}"%"
and p.mainType=#{query.mainType}
and pa.applyName like "%"#{query.applyName}"%"

select * from patent
where companyId=#{companyId} and projectId in

    #{projectId}

(select (case when mainType='发明专利' then 1 else 2 end) `type`,`source`,claimNum,usedCnt,authDate issueDate
from patent where companyId=#{companyId} and year(authDate)<#{year})
union all
(select 1 as type,`source`,claimNum,usedCnt,issueDate
from ICRegistration where companyId=#{companyId} and year(issueDate)<#{year})
union all
(select 2 as type,`source`,claimNum,usedCnt,issueDate
from softRegistration where companyId=#{companyId} and year(issueDate)<#{year})
