SELECT p.data,p.id ,d.docFileTemplateId,pp.pname,pp.beginYear,pp.rdIndex, t.docTemplateName as templateName,d.id as pDocFileId,d.docFileId, d.docFileName FROM p_docFile d LEFT JOIN p_docFile_data p ON d.id = p.pdocFileId LEFT JOIN p_project pp ON d.projectId=pp.id LEFT JOIN docFileTemplate t ON t.id = d.docFileTemplateId LEFT JOIN docFile f ON f.id = d.docFileId LEFT JOIN docFileTemplate t2 ON t2.docFileId = f.id AND t2.defaultTemplate = TRUE WHERE d.id in
#{id}
order by d.stage,d.seq
