mapper.select:
SELECT pt.id,(CASE WHEN pt.startTime IS NOT NULL AND pt.endTime IS NOT NULL THEN TIMESTAMPDIFF(MINUTE ,pt.startTime,pt.endTime) ELSE 0 END) AS `minute`, pt.trialDate,pt.startTime,pt.endTime,pt.unit,pt.deptName,pt.rdYield,pt.planYield FROM p_yield_config pt left join p_docFile_trial pdt on pt.id=pdt.trialId where pt.companyId=#{companyId} and pt.projectId=#{projectId} and pt.trialDate is not null and pdt.id is null
and trialDate >= #{startDate}
and trialDate <= #{endDate}
order by trialDate

select p.rdIndex,p.deptName,p.processSection,p.pname,p.estimateExpense,p.beginDate,p.endDate,p.trialProd,p.beginYear,p.rdTitle,t.planPO, t.actualPO,t.pos,t.trialGroup,t.mainMaterial,t.auxMaterial,t.fuel,t.power,t.gas,t.spare,t.trialDate,t.startTime,t.endTime from p_trialProd t inner join p_project p on t.projectId=p.id and t.companyId=p.companyId and p.hasChild=false where t.companyId=#{companyId} and p.beginYear <=#{query.year} and p.endYear >= #{query.year}
and t.trialDate >= #{query.monthFirstDay} and t.trialDate <= #{query.monthLastDay}
and p.pname like "%"#{query.projectName}"%"

select p.rdIndex,p.deptName,p.processSection,p.pname,p.estimateExpense,p.beginDate,p.endDate,p.trialProd,p.beginYear,p.rdTitle,t.planPO, t.actualPO,t.pos,t.trialGroup,t.mainMaterial,t.auxMaterial,t.fuel,t.power,t.gas,t.spare,t.trialDate,t.startTime,t.endTime,t.unit from p_trialProd t inner join p_project p on t.projectId=p.id and t.companyId=p.companyId and p.hasChild=false where t.companyId=#{companyId} and p.beginYear <=#{query.year} and p.endYear >= #{query.year}
and t.trialDate >= #{query.monthFirstDay} and t.trialDate <= #{query.monthLastDay}
and p.pname like "%"#{query.projectName}"%"

select rdYield,planYield,trialDate,unit,startTime,endTime,deptName
from p_yield_config
where projectId=#{projectId} and `month`=#{month}
